                <div className="flex justify-center space-x-4">
                  <Link
                    to="/doctor/login"
                    className="text-sm text-blue-600 hover:text-blue-500"
                  >
                    Doctor Login
                  </Link>
                  <Link
                    to="/admin/login"
                    className="text-sm text-purple-600 hover:text-purple-500"
                  >
                    Admin Login
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserLogin;

